import React from 'react';

function StudentStatusCard({ status, error }) {
  if (error) return <p className="error">{error}</p>;
  if (!status) return <p>Nhập mã sinh viên ở trên để xem thông tin.</p>;
  
  // SỬA LỖI: Gán giá trị mặc định là mảng rỗng [] cho activeBorrows.
  // Nếu status.activeBorrows là undefined hoặc null, biến activeBorrows sẽ là [].
  // Điều này đảm bảo .length và .map sẽ luôn hoạt động mà không bị lỗi.
  const activeBorrows = status.activeBorrows || [];
  
  return (
    <div>
      {/* CẢI TIẾN: Dùng optional chaining (?.) để tránh lỗi nếu status.student không tồn tại */}
      <h4>Thông tin sinh viên</h4>
      {status.student ? (
        <p><strong>Mã SV:</strong> {status.student.studentId} - <strong>Tên:</strong> {status.student.studentName}</p>
      ) : (
        <p>Không có thông tin sinh viên.</p>
      )}

      {/* SỬA LỖI: Sử dụng biến activeBorrows đã được đảm bảo là một mảng */}
      <h4>Sách đang mượn ({activeBorrows.length}):</h4>
      {activeBorrows.length > 0 ? (
        <ul>
          {activeBorrows.map(b => (
            <li key={b.id}>ID Mượn: {b.id} - Sách: "{b.bookTitle}" (Ngày mượn: {b.borrowDate})</li>
          ))}
        </ul>
      ) : <p>Không có sách nào đang mượn.</p>}
    </div>
  );
}

export default StudentStatusCard;
